    PRIMARY KEY (id),
    UNIQUE KEY uk_check_digit_algorithm_code (code),
    KEY idx_check_digit_algorithm_type (algorithm_type)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

