    `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    PRIMARY KEY (`id`),
    UNIQUE KEY `uk_carrier_code` (`carrier_code`),
    KEY `idx_category` (`carrier_category`),
    KEY `idx_is_obsolete` (`is_obsolete`),
