    KEY idx_script_id (script_id),
    CONSTRAINT fk_unicode_block_script FOREIGN KEY (script_id) REFERENCES language_script(script_id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

