    INDEX idx_gdpr_category (gdpr_category),
    INDEX idx_protection_level (enhanced_protection, encryption_required),
    INDEX idx_active (is_active)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

